#!/usr/bin/python
import argparse


from decoder import parse_raw_transaction, get_abi_methods, create_abi_method_dict, decode_tx_input, get_method_name

# Exports contract ABI in JSON


parser = argparse.ArgumentParser()
parser.add_argument('tx_hash', type=str, help='Transaction Hash')

# tx_hash = '0xc16cf1f97af8f3d174bfcc4c6dad661fad1e8fd9fa7bb496a9ed599f0e70cf64'


# tx_input = ''
raw_tx = ''
# raw_tx = ''

def __main__():

    args = parser.parse_args()

    tx = parse_raw_transaction(raw_tx)
    contract = tx.get_destination()

    abi_json = get_abi_methods(contract)
    abi_methods = create_abi_method_dict(abi_json)

    tx_args = decode_tx_input(tx.get_input(), abi_methods)

    print('Method: ' + get_method_name(tx, abi_methods))
    print('-' * 30)
    for arg in tx_args:
    	print('Argument: ' + arg['name'])
    	print('Type: ' + arg['type'])
    	print('Value: ' + str(arg['value']))
    	print('-' * 30)



    

if __name__ == '__main__':
    __main__()